pragma solidity ^0.5.0;
// this SC work: we stake Dai token fiha and after some itnerval we should earn some Dapp token

// steps : deploy dai --> dapp --> then farm
import "./DappToken.sol" ;
import "./DaiToken.sol"; 

contract TokenFarm { 
    // code here the sc 
    string public name = "Dapp Token Farm" ; 
    // name is state variable : will be stored on the blockchain
    DappToken public dappToken; 
    DaiToken  public daiToken ;
    address public owner;


    address[] public stakers;  // array that keeps track of all addresses that have staked inside the app.
    mapping(address=> uint) public stakingBalance; // investor adr= key, value= the amount of tokens they're staking
    mapping(address => bool) public hasStaked; // pou rune adresse elle retourne un bool if that user has staked. 
    mapping(address=> bool) public  isStaking;


    constructor(DappToken _dappToken, DaiToken _daiToken) public {
        dappToken = _dappToken; 
        daiToken = _daiToken ; 
        owner = msg.sender; // recuperer l'adressee du celui qui a deployer la sc
    }

    //now the corps du SC 
    //1. stakes tokens ( investor puts money on the app) DEPOSIT 
    function stakeTokens(uint _amount) public {

        require(_amount > 0 , "amount cannot be 0"); //if not, the function will fail directly

        // transfer investor toekns (dAI)  from his wallet to this SC 
        daiToken.transferFrom(msg.sender, address(this), _amount);
        //transfer from is a delegated transfer ( i allow someone else to transfer my tokens)
        //par contre tranfer is me spending my tokens. 


        //update staking balance
        stakingBalance[msg.sender]= stakingBalance[msg.sender] + _amount; // on cumule combien ils stake
        stakers.push(msg.sender);

        // add user to stakers array ssi they havent stake dalready 
        if (!hasStaked[msg.sender]){
            stakers.push(msg.sender);
        }

        //update staking status
        isStaking[msg.sender] = true;
        hasStaked[msg.sender] = true;
    }


     //2. Issuing tokens ( called by owner of the sc : investors earning interest ( reawrd) )
    function issueToken() public { 
        require(msg.sender == owner, 'caller must be the owner'); //controlling who can execute the SC. 
        for (uint i=0 ; i<stakers.length; i++){
            //for each investor ( who staked) 
            address recipient = stakers[i]; 
            uint balance = stakingBalance[recipient]; //how much they staked
            if (balance>0){
                dappToken.transfer(recipient, balance); // for each staked dai, u earn the same amount of dapp. 
            }

        }

    }


    //3. unstaking tokens ( Withdraw== retirer) investor removes token from the app 
    function unstakeTokens() public {
        // Fetch staking balance
        uint balance = stakingBalance[msg.sender];

        // Require amount greater than 0
        require(balance > 0, "staking balance cannot be 0");

        // Transfer Mock Dai tokens back to user
        daiToken.transfer(msg.sender, balance);

        // Reset staking balance
        stakingBalance[msg.sender] = 0;

        // Update staking status
        isStaking[msg.sender] = false;
    }


}

// helps access the variable val outside the sc.
